import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import AppNavbar from './AppNavBar';

class Edit extends Component {

    nullFoodItem = {
        description: '',
        calories: ''
    };

    constructor(props) {
        super(props);
        this.state = {
            foodItem: this.nullFoodItem
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async componentDidMount() {
        if (this.props.match.params.id !== 'new') {
            const newFoodItem = await (await fetch(`/food/${this.props.match.params.id}`)).json();
            this.setState({ foodItem: newFoodItem });
        }
    }

    handleChange(event) {
        const target = event.target;
        let newItem = { ...this.state.foodItem };
        newItem[target.name] = target.value;
        this.setState({ newItem });
    }

    async handleSubmit(event) {
        event.preventDefault();
        const { foodItem } = this.state;

        await fetch('/food' + (foodItem.id ? '/' + foodItem.id : ''), {
            method: (foodItem.id) ? 'PUT' : 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(foodItem),
        });
        this.props.history.push('/food');
    }

    render() {
        const { foodItem } = this.state;
        const title = <h2>{foodItem.id ? 'Edit Client' : 'Add Client'}</h2>;

        return <div>
            <AppNavbar />
            <Container>
                {title}
                <Form onSubmit={this.handleSubmit}>
                    <FormGroup>
                        <Label for="description">Description</Label>
                        <Input type="text" name="description" id="description" value={foodItem.description || ''}
                            onChange={this.handleChange} autoComplete="description" />
                    </FormGroup>
                    <FormGroup>
                        <Label for="calories">Calories</Label>
                        <Input type="text" name="calories" id="calories" value={foodItem.calories || ''}
                            onChange={this.handleChange} autoComplete="calories" />
                    </FormGroup>
                    <FormGroup>
                        <Button color="primary" type="submit">Save</Button>{' '}
                        <Button color="secondary" tag={Link} to="/food">Cancel</Button>
                    </FormGroup>
                </Form>
            </Container>
        </div>
    }
}

export default withRouter(Edit);
