import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import AppNavbar from './AppNavBar';

class Edit extends Component {

    nullFoodItem = {
        description: '',
        calories: ''
    };

    constructor(props) {
        super(props);
        this.state = {
            foodItem: this.nullFoodItem
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async componentDidMount() {
        if (this.props.match.params.id !== 'new') {
            const newFoodItem = await (await fetch(`/food/${this.props.match.params.id}`)).json();
            this.setState({ foodItem: newFoodItem });
        }
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let item = { ...this.state.foodItem };
        item[name] = value;
        this.setState({ item });
    }

    async handleSubmit(event) {
        event.preventDefault();
        const { foodItem, item } = this.state;

        console.log(this.state);
        console.log(foodItem);
        console.log(item);

        await fetch('/food' + (foodItem.id ? '/' + foodItem.id : ''), {
            method: (foodItem.id) ? 'PUT' : 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(foodItem),
        });
        this.props.history.push('/food');
    }

    render() {
        const { foodItem } = this.state;
        const title = <h2>{foodItem.id ? 'Edit Client' : 'Add Client'}</h2>;

        return <div>
            <AppNavbar />
            <Container>
                {title}
                <Form onSubmit={this.handleSubmit}>
                    <FormGroup>
                        <label for="description">Description</label>
                        <input type="text" name="description" id="description"
                            onChange={this.handleChange} autoComplete="description" />
                    </FormGroup>
                    <FormGroup>
                        <label for="calories">Calories</label>
                        <input type="text" name="calories" id="calories"
                            onChange={this.handleChange} autoComplete="10" />
                    </FormGroup>
                    <FormGroup>
                        <button color="primary" type="submit">Save</button>{' '}
                        <button color="secondary" tag={Link} to="/food">Cancel</button>
                    </FormGroup>
                </Form>
            </Container>
        </div>
    }
}

export default withRouter(Edit);
